INSERT INTO `providers` VALUES (1,'阳光书屋','神秘组织，不崇拜星辰诸神',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 16:24:08'),(2,'天驱武士团','铁甲依然在',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 16:24:10'),(3,'辰月教','星辰与月的旗帜飘扬在九州大地上',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 16:24:13'),(4,'天罗','我躲在这里，手握着刀丝',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 16:24:16'),(5,'阳光书屋',NULL,NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-07 14:53:51'),(6,'超星114','',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-17 08:59:38'),(7,'中文在线','',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 09:43:26'),(8,'阳光188','',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-17 08:59:32'),(9,'超新星图书馆','好多好多书哦',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 09:02:04'),(10,'深圳少年儿童图书馆','好多好多好多好多书',NULL,NULL,NULL,NULL,'1970-01-01 00:00:00','2012-08-18 08:43:50'),(11,'超星数字图书馆','中国规模最大的数字图书馆解决方案提供商和数字图书资源提供商。超星免费为阳光书屋提供了电子图书，并帮助我们进行了格式转换。',NULL,NULL,NULL,NULL,'2012-08-18 16:20:15','2012-08-18 16:20:15'),(12,'中文在线','中文在线2000年成立于清华大学，是中国数字出版的开创者之一。中文在线与国内400余家版权机构合作，签约知名作家、畅销书作者2000余位，拥有驻站网络作者超过10万名。中文在线为阳光书屋提供了电子图书，并帮助我们进行了格式转换。',NULL,NULL,NULL,NULL,'2012-08-18 16:20:30','2012-08-18 16:20:30'),(13,'深圳少年儿童图书馆','深圳及港澳地区唯一专门为少儿、家长及教育工作者服务的文献信息中心。深圳少年儿童图书馆免费为阳光书屋提供了图书精选指导。',NULL,NULL,NULL,NULL,'2012-08-18 16:20:45','2012-08-18 16:20:45'),(14,'海天出版社','深圳经济特区最资深的一家出版社，国内较有实力的出版实体。海天出版社免费为阳光书屋提供了《青春读书课》系列读本电子版。',NULL,NULL,NULL,NULL,'2012-08-18 16:23:17','2012-08-18 16:23:17'),(15,'北京四中网校','北京四中的远程教育机构，多项大奖获得者，目前在全国已设立了二百四十余家分支机构。北京四中网校免费为阳光书屋提供了优质的教学资源。',NULL,NULL,NULL,NULL,'2012-08-18 16:23:42','2012-08-18 16:23:42'),(16,'严凌君与钟惠群夫妇','深圳育才中学语文教师的严凌君老师，5年前自创青春读书课，用古今中外的名家名篇，滋润了几百位中学生的青春。严凌君老师还选编出一套7卷14本300万字的《青春读书课》人文读本，在图书市场上产生了轰动效应。\r\n\r\n严老师无偿将自己的著作《青春读书课》读本捐赠给阳光书屋，作为阅读课资料使用，还与钟老师一同在2011年8月前往甘肃，帮助培训当地教师如何上好阅读课。',NULL,NULL,NULL,NULL,'2012-08-18 16:24:00','2012-08-18 16:24:00'),(17,'阳光书屋','21世纪是科技的世纪，而在目前中国的乡村，现代技术对那里的学生来说是那么的遥不可及，更不要说通过技术去获得课内外丰富的学习资源和机会。但他们同样拥有渴求知识的双眸，同样拥有获得平等教育机会的权利。阳光书屋希望通过“一人一机”的平板电脑项目为乡村的孩子们带去知识，视野，好奇心与梦想，让他们前方的道路洒满阳光，用科技促进科技的世纪的教育！ ',NULL,NULL,NULL,NULL,'2012-08-18 17:34:28','2012-08-18 17:34:28'),(18,'北师大教育技术学院','',NULL,NULL,NULL,NULL,'2012-10-28 15:46:42','2012-10-28 15:46:42');
